import React ,{useState} from 'react'
import Todolist from './Todolist'

 function Todo() {
    const [todo, setTodo]= useState({title:"",done:false});
    const [todoArr,setTodoArr] =useState([])

    let todos =localStorage.hasOwnProperty("todos")? JSON.parse(localStorage.getItem("todos")):[];


    const onChange = (event ) =>{
        let {value} =event.target
        let obj ={}
        obj["title"] =value
        obj["done"] = false
        setTodo(obj)
    }

       const createTodo =(event)=>{
        const {name} =event.target
        if(todo.key==="Enter" || name === "addTodo"){
          if(todo.title !== ""){
            todos.unshift(todo)
            localStorage.setItem('todos', JSON.stringify(todos))
            setTodo({title:"",done:false})
          }
        }
       }

       const completeTodo =(i) => {
        if(todos[i]["done"] !== true){
          todos[i]["done"] =true
          localStorage.setItem("todos" ,JSON.stringify(todos))
          setTodoArr(todos)
        }
       }
  return (
    <>
    <div>
        <h1>All Tasks</h1>
        <div>
                  <input id="input" type="text" name="add" class="textinput" placeholder="Add a new task" value={todo.title} onKeyPress={createTodo} onChange={onChange}/>
                  <button id="addbtn" type='button' class="button" onClick={createTodo} >Add</button>

       </div> 
       <Todolist todoArr ={todoArr}
                 completeTodo ={completeTodo} />
       <div>
           <h2 id="h2">Seems like a quiet day</h2>
        </div>
    </div>

    </>
  )
}


export default Todo
